import React from 'react';
import YouTube from 'react-youtube';

function Player({ state, onPlayerReadyRef, setVolume, setIsPlaying, setCurrentTime, setDuration, currentTime }) { // Changed: Add currentTime as a prop
    const playerRef = React.useRef(null);
    const [videoId, setVideoId] = React.useState('');
    const intervalRef = React.useRef(null);

    React.useEffect(() => {
        const playedRecord = state.find(record => record.isPlayed);
        if (playedRecord) {
            const videoUrl = playedRecord.youtubeUrl;
            const regex = /(?:https?:\/\/)?(?:www\.)?(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&]+)/;
            const match = videoUrl.match(regex);
            const newVideoId = match ? match[1] : null;
            setVideoId(newVideoId);

            if (intervalRef.current) {
                clearInterval(intervalRef.current);
            }

            setCurrentTime(0);
            setIsPlaying(false);
            setDuration(0);
        }
    }, [state, setCurrentTime, setIsPlaying, setDuration]);

    React.useEffect(() => {
        if (playerRef.current && playerRef.current.seekTo) {
            playerRef.current.seekTo(currentTime); // Use currentTime prop
        }
    }, [currentTime]);

    const opts = {
        height: '200',
        width: '350',
        playerVars: {
            autoplay: 1,
        },
    };

    function onPlayerReady(event) {
        playerRef.current = event.target;
        if (onPlayerReadyRef) {
            onPlayerReadyRef.current = event.target;
        }
        setVolume(event.target.getVolume());
        setIsPlaying(event.target.getPlayerState() === 1);
        setDuration(event.target.getDuration());

        intervalRef.current = setInterval(() => {
            if (playerRef.current && playerRef.current.getCurrentTime) {
                setCurrentTime(playerRef.current.getCurrentTime());
            }
        }, 1000);
    }

    function onPlayerStateChange(event) {
        setIsPlaying(event.data === 1);
    }

    return (
        <div className="youtube--player--container" id="player" style={{ visibility: 'hidden' }}>
            {videoId && (
                <YouTube
                    videoId={videoId}
                    opts={opts}
                    onReady={onPlayerReady}
                    onStateChange={onPlayerStateChange}
                />
            )}
        </div>
    );
}

export default Player;
