import React from 'react';
import Navbar from './components/Navbar';
import Shelf from './components/Shelf';
import Filter from './components/Filter';
import Jukebox from './components/Jukebox';
import Player from './components/Player';
import SocialFooter from './components/SocialFooter';

import local_data from './data.js';
import './index.css';

function App() {  
  const [nowPlaying, setNowPlaying] = React.useState(local_data);
  const inputRef = React.useRef(null);
  const playerRef = React.useRef(null);

  // Changes start
  const [isPlaying, setIsPlaying] = React.useState(true);
  const [volume, setVolume] = React.useState(50); // Initial volume
  // Changes end

  React.useEffect(() => {
      fetch('http://localhost:8000')
          .then(response => {
              if (!response.ok) {
                  throw new Error('Network response was not ok');
              }
              return response.json();
          })
          .then(data => {
              setNowPlaying(data);
          })
          .catch(error => {
              console.error('There was a problem with the fetch operation:', error);
          });
  }, []);

  React.useEffect(() => {
      const handleClick = () => {
          if (inputRef.current) {
              inputRef.current.focus();
          }
      };

      document.addEventListener('click', handleClick);

      return () => {
          document.removeEventListener('click', handleClick);
      };
  }, []);

  function handleClick(id) {
      setNowPlaying(prevNowPlaying => {
          return prevNowPlaying.map((Record) => {
              return Record.id === id ? {...Record, isPlayed: true} : {...Record, isPlayed: false};
          });
      });
  }

  function handleSearchChange(searchString) {
      setNowPlaying(prevNowPlaying => {
          if (!searchString) {
              return prevNowPlaying.slice().sort((a, b) => a.id - b.id);
          } else {
              return prevNowPlaying.slice().sort((a, b) => {
                  const aIncludes = a.artist.toLowerCase().includes(searchString.toLowerCase());
                  const bIncludes = b.artist.toLowerCase().includes(searchString.toLowerCase());

                  if (aIncludes && !bIncludes) return -1;
                  if (!aIncludes && bIncludes) return 1;
                  return 0;
              });
          }
      });
  }

  const handlePlay = () => {
      if (playerRef.current) {
          playerRef.current.playVideo();
          setIsPlaying(true); // Changes
      }
  };

  const handlePause = () => {
      if (playerRef.current) {
          playerRef.current.pauseVideo();
          setIsPlaying(false); // Changes
      }
  };

  const handleVolumeChange = (value) => {
      if (playerRef.current) {
          playerRef.current.setVolume(value);
          setVolume(value); // Changes
      }
  };

  return (
    <div>
        <Navbar
            onPlay={handlePlay}
            onPause={handlePause}
            onVolumeChange={handleVolumeChange}
            isPlaying={isPlaying} // Changes
            volume={volume} // Changes
        />
        <Shelf data={nowPlaying} handleClick={handleClick}/>
        <Filter 
            data={nowPlaying} 
            onSearchChange={handleSearchChange}
            inputRef={inputRef} 
        />
        <Player state={nowPlaying} onPlayerReadyRef={playerRef} setVolume={setVolume} setIsPlaying={setIsPlaying} /> {/* Changes */}
        <Jukebox state={nowPlaying}/>
        <SocialFooter />
    </div>
  );
}

export default App;
