import React from 'react';

function Navbar({ onPlay, onPause, onVolumeChange, isPlaying, volume, currentTime, duration, onTimeChange }) {
    const [isPlayingState, setIsPlayingState] = React.useState(isPlaying);

    React.useEffect(() => {
        setIsPlayingState(isPlaying);
    }, [isPlaying]);

    const handleClick = () => {
        if (isPlayingState) {
            onPause();
        } else {
            onPlay();
        }
        setIsPlayingState(!isPlayingState);
    };

    return (
        <nav className="navbar">
            <button onClick={handleClick}>{isPlayingState ? 'Pause' : 'Play'}</button>
            <input
                type="range"
                min="0"
                max="100"
                value={volume}
                onChange={(e) => onVolumeChange(e.target.value)}
            />
            <input
                type="range"
                min="0"
                max={duration}
                value={currentTime}
                onChange={(e) => onTimeChange(Number(e.target.value))}
            />
        </nav>
    );
}

export default Navbar;
