import React from 'react';

function Navbar({ onPlay, onPause, onVolumeChange, isPlaying, volume, currentTime, duration, onSeek }) { // Changes: Added currentTime, duration, and onSeek as props
    function handleClick() {
        isPlaying ? onPause() : onPlay()
    }
    function handleTimelineChange(event) { // New: Handle timeline change
        const time = (event.target.value / 100) * duration;
        onSeek(time);
    }

    return (
        <nav className="navbar">
            <button onClick={handlePlayPauseClick}>{isPlaying ? "Pause" : "Play"}</button>
            <input 
                type="range" 
                min="0" 
                max="100" 
                value={(currentTime / duration) * 100} // Changes
                onChange={handleTimelineChange} // New: Handle timeline change
            />
            <input 
                type="range" 
                min="0" 
                max="100" 
                value={volume} // Changes
                onChange={(e) => onVolumeChange(e.target.value)} 
            />
        </nav>
    );
}
    
export default Navbar;
