import React from 'react';
import YouTube from 'react-youtube';


function Player({ state, onPlayerReadyRef }) { // Changed: Add onPlayerReadyRef as a prop
    const playerRef = useRef(null); // Changed: Initialize playerRef
    const [videoId, setVideoId] = React.useState('');

    React.useEffect(() => {
        const playedRecord = state.find(record => record.isPlayed);
        if (playedRecord) {
            const videoUrl = playedRecord.youtubeUrl;
            const regex = /(?:https?:\/\/)?(?:www\.)?(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&]+)/;
            const match = videoUrl.match(regex);
            const newVideoId = match ? match[1] : null;
            setVideoId(newVideoId);
        }
    }, [state]);

    const opts = {
        height: '200',
        width: '350',
        playerVars: {
            autoplay: 1,
        },
    };

    function onPlayerReady(event) { // Changed: Define onPlayerReady function
        playerRef.current = event.target; // Changed: Set playerRef to the player instance
        if (onPlayerReadyRef) { // Changed: If onPlayerReadyRef prop is provided, set it to the player instance
            onPlayerReadyRef.current = event.target;
        }
    }

    function onPlayerStateChange(event) {
        console.log('Player state change', event.data);
    }

    return (
        <div className="youtube--player--container" id="player">
            {videoId && (
                <YouTube
                    videoId={videoId}
                    opts={opts}
                    onReady={onPlayerReady} // Changed: Pass onPlayerReady to YouTube component
                    onStateChange={onPlayerStateChange}
                />
            )}
        </div>
    );
}
