function Navbar({ onPlay, onPause, onVolumeChange }) {
    const [isPlaying, setIsPlaying] = React.useState(true)

    function handleClick(onPlay, onPause, isPlaying) {
        isPlaying ? onPause() : onPlay();
        setIsPlaying(prevIsPlaing => !prevIsPlaing)
    }

    return (
        <nav className="navbar">
            <button onClick={handleClick}>Play</button>
            <input type="range" min="0" max="100" onChange={(e) => onVolumeChange(e.target.value)} />
        </nav>
    );
}

export default Navbar;
