import React from 'react';

function Navbar({ onPlay, onPause, onVolumeChange, isPlaying, volume, currentTime, duration }) {
    const [isPlayingState, setIsPlayingState] = React.useState(isPlaying);

    React.useEffect(() => {
        setIsPlayingState(isPlaying);
    }, [isPlaying]);

    const handleClick = () => {
        isPlayingState ? onPause() : onPlay()

        setIsPlayingState(!isPlayingState);
    };

    return (
        <nav className="navbar">
            <button onClick={handleClick}>{isPlayingState ? 'Pause' : 'Play'}</button>
            <input
                type="range"
                min="0"
                max="100"
                value={volume}
                onChange={(e) => onVolumeChange(e.target.value)}
            />
            <input
                type="range"
                min="0"
                max={duration}
                value={currentTime}
                readOnly
            />
        </nav>
    );
}

export default Navbar;
